func TestEquihash2(t *testing.T) {
	h, _ := hex.DecodeString("")
	sol, _ := hex.DecodeString("0510f68e34231c0a03b12c86fa7fd99dfa21d1a6d7b3263e435a359f04d6fe2d93a305d5fb51e28dd63ce788a629d5f4effd88129eed38d4bbfb3641ff7955aa2b4df800")

	valid := Verify(h, sol)
	fmt.Println(valid)
	if !valid {
		t.Errorf("Must be correct solution")
	}

}

